<?phpinclude('inc/init.inc.php');include('library/phpmailer/class.phpmailer.php');/** * Pega os dados do imóvel */$mapper = new Mapper();$mapper->setDbTable(new Imovel());$mapper->setWhere('id = "' . $_GET['id_imovel'] . '"');$row = $mapper->getRow();/** * Pega o tipo do imóvel */$mapper_tipo = new Mapper();$mapper_tipo->setDbTable(new ImovelTipo());$mapper_tipo->setWhere('id = "' . $row->tipo_imovel_id . '"');$tipo = $mapper_tipo->getRow();/** * Pega a cidade do imóvel */$mapper_city = new Mapper();$mapper_city->setDbTable(new ImovelCidade());$mapper_city->setWhere('id = "' . $row->cidade_imovel . '"');$city = $mapper_city->getRow()->cidade;/** * Pega a uf do imóvel */$mapper_uf = new Mapper();$mapper_uf->setDbTable(new ImovelUf());$mapper_uf->setWhere('id = "' . $row->uf_imovel . '"');$uf = $mapper_uf->getRow()->uf;/** * Pega o bairro do imóvel */$mapper_bairro = new Mapper();$mapper_bairro->setDbTable(new ImovelBairro());$mapper_bairro->setWhere('id = "' . $row->bairro_imovel . '"');$bairro = $mapper_bairro->getRow()->bairro;/** * Pega as fotos do imovel */$mapper_fotos = new Mapper();$mapper_fotos->setDbTable(new ImovelAlbum());$mapper_fotos->setWhere('imovel_id = "' . $row->id . '"');$mapper_fotos->setOrder("id ASC");$rows_fotos = $mapper_fotos->getRows();if ($row->valor_aluguel != "0.00")    $val_aluguel = "<strong>Valor Aluguel :</strong> " . number_format($row->valor_aluguel, 2, ',', '.').'<br>';else    $val_aluguel = "";if ($row->valor_venda != "0.00")    $val_venda = "<strong>Valor Venda :</strong> " . number_format($row->valor_venda, 2, ',', '.').'<br>';else    $val_venda = "";$mails = explode(',', $_GET['emails']);$imagens   = "";$ft_perfil = $_GET['foto'];$tel       = $_GET['tel'];$logotipo  = "";$logomarca = "";$telefone  = "";$mensagem  = "";$info_ad   = "";if($_GET['mensagem'] != "")    $mensagem = "<tr><td>".utf8_decode($_GET['mensagem'])."</td></tr><tr><td>&nbsp;</td></tr>";if($tel != "")    $telefone = "<strong>Telefone Corretor: </strong>".$tel."<br>";if($ft_perfil != ""){    $ft_perfil  = URL.'static/uploads/fotos/'.$_GET['login'].'/'.$ft_perfil;    $logomarca = '<tr><td style="text-align:center; padding: 10px 0;"><img src="'.URL.'library/phpthumb/phpThumb.php?src='.$ft_perfil.'&amp;h=100" /></td>';}else{     $logomarca = '<tr><td style="text-align:center; padding: 10px 0;"><img src="'.URL.'static/img/logo_email.jpg" /></td>';}foreach ($rows_fotos as $fotos) {    if($fotos->foto != ""){        $img = URL . 'static/uploads/imoveis/' . $_GET['login2'] . '/' . $row->titulo_url . '/' . $fotos->foto;        $imagens .= '<tr><td><img style="border: 4px solid #0B3458; margin-bottom: 15px;" src="'.URL.'library/phpthumb/phpThumb.php?src='.$img.'&amp;w=400" /></td><td><strong>'.$fotos->legenda.'</strong></td></tr>';    }}if($row->informacoes_adicionais != "")    $info_ad = '<strong>Informações Adicionais:</strong> ' . $row->informacoes_adicionais . '<br>';    $text = '<table cellpadding="0" cellspacing="0" border="0" style="background-color: #132843; width: 600px;">'.$logomarca.'</tr></table><table cellpadding="0" cellspacing="0" border="0" style="border: 4px solid #132843; padding: 10px; width: 600px;">    <tr>        <td>O Corretor ' . $_GET['nome'] . ' lhe enviou um imóvel pelo site <a href="http://www.suafocalpoint.com/new">Suafocalpoint.</a></td>    </tr>    <tr>        <td>&nbsp;</td>    </tr>    '.$mensagem.'    <tr>        <td style="font-family: Arial; font-size: 13px;">            '.$telefone.'            <strong>E-mail Corretor:</strong> ' . $_GET['email'] . '<br>            <strong>Título:</strong> ' . $row->codigo_imovel . ' - ' . $row->titulo . '<br>            <strong>Tipo:</strong> ' . $tipo->nome . '<br>            <strong>Localização:</strong> ' . $bairro . ' - ' . $city . ' - ' . $uf . '<br>            ' . $val_aluguel . $val_venda . '            <strong>Área Construída:</strong> ' . $row->area_construida . ' m²<br />            <strong>Área Externa:</strong> ' . $row->area_externa . ' m²<br />            <strong>Terreno:</strong> ' . $row->terreno . ' m²<br />            <strong>Piscina:</strong> ' . $row->piscina . '<br />            <strong>Mobiliado:</strong> ' . $row->mobiliado . '<br />            <strong>Portaria 24h:</strong> ' . $row->portaria_24h . '<br />            <strong>Varanda:</strong> ' . $row->varanda . '<br />            '.$info_ad.'                <br><br>            '.$imagens.'        </td>    </tr>    </table>    <table cellpadding="0" cellspacing="0" border="0" style="background-color: #132843; width: 600px; height: 100px;">    <tr>            <td style="text-align:center;">            <img src="'.URL.'static/img/logo_email.jpg" />        </td>    </tr></table>';$retorno = "";$mail = new PHPMailer(true);$mail->IsSMTP(); // Define que a mensagem será SMTP$mail->Host = HOST; // Endereço do servidor SMTP$mail->From = $_GET['email']; // Seu e-mail$mail->FromName = $_GET['nome'];$mail->Sender = EMAIL; // Seu e-mail$mail->IsHTML(true);$mail->Subject = 'Envio de Imóvel - ' . SITE;$mail->Body = $text;foreach ($mails as $email) {    $mail->AddAddress(trim($email));        if($mail->Send())        $retorno .= "e-mail enviado com sucesso para: ".$email."<br>";    else        $retorno .= "erro ao enviar e-mail para: ".$email.".<br>";        $mail->ClearAllRecipients();    $mail->ClearAttachments();}echo $retorno;?>